-- КОРОТКИЙ ДАМП (для Command Bar) - показує тільки основні координати
for _, obj in ipairs(workspace:GetChildren()) do if obj.Name ~= "Camera" and obj.Name ~= "Terrain" then if obj:IsA("BasePart") then print(string.format("[%s] %s: %.1f, %.1f, %.1f", obj.ClassName, obj.Name, obj.Position.X, obj.Position.Y, obj.Position.Z)) elseif obj:IsA("Model") then local pivot = obj:GetPivot() print(string.format("[Model] %s: %.1f, %.1f, %.1f", obj.Name, pivot.Position.X, pivot.Position.Y, pivot.Position.Z)) if obj.PrimaryPart then print(string.format("  └─ PrimaryPart: %.1f, %.1f, %.1f", obj.PrimaryPart.Position.X, obj.PrimaryPart.Position.Y, obj.PrimaryPart.Position.Z)) end end end end
